import React, { createContext, useContext, useReducer } from 'react';
import { useToast } from './ToastContext';
// TODO: Uncomment when API is ready
// import { 
//   getCart, 
//   addToCart as addToCartAPI, 
//   updateCartItem, 
//   removeFromCart as removeFromCartAPI, 
//   clearCart as clearCartAPI,
//   handleApiError 
// } from '../services/api';

const CartContext = createContext();

const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      const existingItem = state.items.find(item => item.id === action.payload.id);
      if (existingItem) {
        return {
          ...state,
          items: state.items.map(item =>
            item.id === action.payload.id
              ? { ...item, quantity: item.quantity + 1 }
              : item
          )
        };
      }
      return {
        ...state,
        items: [...state.items, { ...action.payload, quantity: 1 }]
      };
    
    case 'REMOVE_FROM_CART':
      return {
        ...state,
        items: state.items.filter(item => item.id !== action.payload)
      };
    
    case 'UPDATE_QUANTITY':
      return {
        ...state,
        items: state.items.map(item =>
          item.id === action.payload.id
            ? { ...item, quantity: action.payload.quantity }
            : item
        ).filter(item => item.quantity > 0)
      };
    
    case 'CLEAR_CART':
      return {
        ...state,
        items: []
      };
    
    default:
      return state;
  }
};

export const CartProvider = ({ children }) => {
  const [state, dispatch] = useReducer(cartReducer, { 
    items: [], 
    loading: false, 
    error: null 
  });
  const { addToast } = useToast();

  // Load cart data from API on component mount
  useEffect(() => {
    loadCartFromAPI();
  }, []);

  /**
   * Load cart data from API
   * API Call: GET /api/v1/cart
   */
  const loadCartFromAPI = async () => {
    try {
      dispatch({ type: 'SET_LOADING', payload: true });
      const cartData = await getCart();
      dispatch({ type: 'SET_CART_DATA', payload: cartData });
    } catch (error) {
      handleApiError(error, (err) => {
        dispatch({ type: 'SET_ERROR', payload: err.message });
        addToast('Failed to load cart data', 'error');
      });
    }
  };

  /**
   * Add product to cart
   * API Call: POST /api/v1/cart/items
   * @param {Object} product - Product to add
   */
  const addToCart = async (product) => {
    try {
      // Optimistic update for better UX
      dispatch({ type: 'ADD_TO_CART', payload: product });
      
      // API call to add item to cart
      await addToCartAPI({
        product_id: product.id,
        quantity: 1,
        price: product.price
      });
      
      addToast(`${product.name} added to cart`, 'success');
    } catch (error) {
      // Revert optimistic update on error
      const existingItem = state.items.find(item => item.id === product.id);
      if (existingItem && existingItem.quantity > 1) {
        dispatch({ type: 'UPDATE_QUANTITY', payload: { id: product.id, quantity: existingItem.quantity - 1 } });
      } else {
        dispatch({ type: 'REMOVE_FROM_CART', payload: product.id });
      }
      
      handleApiError(error, (err) => {
        addToast(`Failed to add ${product.name} to cart`, 'error');
      });
    }
  };

  /**
   * Remove product from cart
   * API Call: DELETE /api/v1/cart/items/{itemId}
   * @param {string|number} productId - Product ID to remove
   */
  const removeFromCart = async (productId) => {
    try {
      const item = state.items.find(item => item.id === productId);
      
      // Optimistic update
      dispatch({ type: 'REMOVE_FROM_CART', payload: productId });
      
      // API call to remove item from cart
      await removeFromCartAPI(productId);
      
      if (item) {
        addToast(`${item.name} removed from cart`, 'error');
      }
    } catch (error) {
      // Revert optimistic update on error
      loadCartFromAPI();
      
      handleApiError(error, (err) => {
        addToast('Failed to remove item from cart', 'error');
      });
    }
  };

  /**
   * Update product quantity in cart
   * API Call: PUT /api/v1/cart/items/{itemId}
   * @param {string|number} productId - Product ID
   * @param {number} quantity - New quantity
   */
  const updateQuantity = async (productId, quantity) => {
    try {
      const oldQuantity = state.items.find(item => item.id === productId)?.quantity || 0;
      
      // Optimistic update
      dispatch({ type: 'UPDATE_QUANTITY', payload: { id: productId, quantity } });
      
      // API call to update cart item quantity
      await updateCartItem(productId, quantity);
      
    } catch (error) {
      // Revert optimistic update on error
      const oldItem = state.items.find(item => item.id === productId);
      if (oldItem) {
        dispatch({ type: 'UPDATE_QUANTITY', payload: { id: productId, quantity: oldItem.quantity } });
      }
      
      handleApiError(error, (err) => {
        addToast('Failed to update quantity', 'error');
      });
    }
  };

  /**
   * Clear entire cart
   * API Call: DELETE /api/v1/cart
   */
  const clearCart = async () => {
    try {
      // Optimistic update
      dispatch({ type: 'CLEAR_CART' });
      
      // API call to clear cart
      await clearCartAPI();
      
      addToast('Cart cleared', 'error');
    } catch (error) {
      // Revert optimistic update on error
      loadCartFromAPI();
      
      handleApiError(error, (err) => {
        addToast('Failed to clear cart', 'error');
      });
    }
  };

  const getTotalPrice = () => {
    return state.items.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const getTotalItems = () => {
    return state.items.reduce((total, item) => total + item.quantity, 0);
  };

  return (
    <CartContext.Provider value={{
      items: state.items,
      loading: state.loading,
      error: state.error,
      addToCart,
      removeFromCart,
      updateQuantity,
      clearCart,
      getTotalPrice,
      getTotalItems,
      loadCartFromAPI
    }}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
