import React from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();

  const handleAddToCart = (e) => {
    e.preventDefault();
    addToCart(product);
  };

  return (
    <div className="bg-gray-800/90 backdrop-blur-sm rounded-xl shadow-2xl shadow-gray-900/50 overflow-hidden hover:shadow-2xl hover:shadow-red-500/20 transition-all duration-500 group transform hover:-translate-y-2 hover:scale-105">
      <div className="aspect-square overflow-hidden relative">
        <img
          src={product.image}
          alt={product.name}
          className="w-full h-full object-cover group-hover:scale-110 transition-transform duration-500"
          onError={(e) => {
            e.target.src = `https://via.placeholder.com/400x400/374151/ffffff?text=${encodeURIComponent(product.name)}`;
          }}
        />
        <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
      </div>
      
      <div className="p-6">
        <h3 className="text-lg font-semibold text-white mb-2 line-clamp-2">
          {product.name}
        </h3>
        
        <div className="flex items-center justify-between mb-4">
          <span className="text-2xl font-bold text-white">
            ${product.price.toLocaleString()}
          </span>
          <span className="text-sm text-gray-400 capitalize">
            {product.category}
          </span>
        </div>
        
        <div className="flex space-x-3">
          <Link
            to={`/product/${product.id}`}
            className="flex-1 bg-gray-700/50 backdrop-blur-sm text-gray-300 px-4 py-2.5 rounded-lg text-center text-sm font-medium hover:bg-gray-600/70 transition-all duration-300 hover:scale-105"
          >
            View Details
          </Link>
          <button
            onClick={handleAddToCart}
            className="flex-1 bg-gradient-to-r from-red-600 to-red-700 text-white px-4 py-2.5 rounded-lg text-sm font-medium hover:from-red-700 hover:to-red-800 transition-all duration-300 hover:scale-105 shadow-lg hover:shadow-red-500/25"
          >
            Add to Cart
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
